package Assignments;

public class PositiveOrNegative {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       int a=10;
       int b=-20;
       
       if (a>=0)
       {
    	   System.out.println("a is positive");
       }
       if(b>0)
       {
    	   System.out.println("b is positive");
       }
       else
       {
    	   System.out.println("b is negative");
       }
	}

}
